And here's where all the magic happens:
```csharp
using Aspire.Hosting.Docker;

namespace IHFiction.AppHost;

internal static class ProductionConfigExtensions
{
    const string AdminNetwork = "t3_proxy";
    const string ContainerNetwork = "containers";
    const string FrontEndNetwork = "ihf_proxy";

    public static IResourceBuilder<DockerComposeEnvironmentResource> ConfigureSwarmCompose(
        this IDistributedApplicationBuilder builder) => builder.AddDockerComposeEnvironment("internal")
            .WithDashboard(dash => dash.PublishAsDockerComposeService((_, service) => service.Networks.Add(AdminNetwork)))
            .ConfigureComposeFile(file =>
            {
                file.AddNetwork(new()
                {
                    Name = FrontEndNetwork,
                    External = true
                })
                .AddNetwork(new()
                {
                    Name = AdminNetwork,
                    External = true
                })
                .AddNetwork(new()
                {
                    Name = ContainerNetwork
                });

                file.Secrets.Add("keycloak-conf", new() { File = "./secrets/keycloak.conf" });
                file.Secrets.Add("mongodb-root-pass", new() { File = "./secrets/mongodb-root-pass.secret" });
                file.Secrets.Add("postgres-pass", new() { File = "./secrets/postgres-pass.secret" });

                file.Secrets.Add("ConnectionStrings__fiction-db", new() { File = "./secrets/conn-fiction-db.secret" });
                file.Secrets.Add("ConnectionStrings__stories-db", new() { File = "./secrets/conn-stories-db.secret" });

                file.Secrets.Add("Authentication__Schemes__Keycloak__ClientSecret", new() { File = "./secrets/keycloak-frontend-client.secret" });
                file.Secrets.Add("KeycloakAdminClientOptions__AuthClientSecret", new() { File = "./secrets/keycloak-admin-client.secret" });

                // Cleanup noise for swarm spec
                foreach (var (_, service) in file.Services)
                {
                    service.DependsOn.Clear(); // BUG: long-format of depends on is incompatible with swarm parser, and it's ignored anyways when short-form
                    service.Expose = []; // Expose is ignored in swarm
                    service.Restart = null; // Restart policy is ignored in swarm for containers
                }
            })
            .WithProperties(props =>
            {
                props.DefaultNetworkName = ContainerNetwork;
                props.DefaultContainerRegistry = builder.Configuration["Registry"];
            });

    public static IDistributedApplicationBuilder ApplyProductionConfig(
        this IDistributedApplicationBuilder builder,
        IResourceBuilder<PostgresServerResource> postgres,
        IResourceBuilder<MongoDBServerResource> mongo,
        IResourceBuilder<KeycloakResource> keycloak,
        IResourceBuilder<ProjectResource> migrations,
        IResourceBuilder<ProjectResource> fictionApi,
        IResourceBuilder<ProjectResource> webClient)
    {
        postgres.PublishAsDockerComposeService((_, service) =>
        {
            service.Environment.Remove("POSTGRES_PASSWORD");
            service.Environment["POSTGRES_PASSWORD_FILE"] = "/run/secrets/postgres-pass";

            service.Secrets.Add(new() { Source = "postgres-pass" });

            service.Healthcheck = new()
            {
                Test = ["CMD-SHELL", "pg_isready -U postgres || exit 1"],
                Interval = "10s", // Bug: this should be optional in swarm
                Timeout = "5s", // Bug: this should be optional in swarm
                Retries = 12,
                StartPeriod = "0s" // Bug: this should be optional in swarm
            };
        });

        mongo.PublishAsDockerComposeService((_, service) =>
        {
            service.Environment.Remove("MONGO_INITDB_ROOT_PASSWORD");
            service.Environment["MONGO_INITDB_ROOT_PASSWORD_FILE"] = "/run/secrets/mongodb-root-pass";

            service.Secrets.Add(new() { Source = "mongodb-root-pass" });
        });

        keycloak.PublishAsDockerComposeService((_, service) =>
        {
            service.Environment.Remove("KC_BOOTSTRAP_ADMIN_PASSWORD");

            service.Networks.Add(FrontEndNetwork);
            service.Secrets.Add(new() { Source = "keycloak-conf", Target = "/opt/keycloak/conf/keycloak.conf", Mode = 0444 });

            service.Command = ["start"];
        });

        migrations.PublishAsDockerComposeService((res, service) =>
        {
            service.Environment.Remove("ConnectionStrings__fiction-db");
            service.Environment.Remove("ConnectionStrings__stories-db");

            if (builder.Configuration["SecretsPath"] is string secretsPath)
                service.Environment["SecretsPath"] = secretsPath;

            service.Secrets.Add(new() { Source = "ConnectionStrings__fiction-db" });
            service.Secrets.Add(new() { Source = "ConnectionStrings__stories-db" });

            if (builder.Configuration["Registry"] is string registry)
                service.Image = $"{registry}/{service.Name}:latest";

            if (res.TryGetEndpoints(out var endpoints))
            {
                foreach (var endpoint in endpoints)
                {
                    if (endpoint.Name == "https") res.Annotations.Remove(endpoint);
                    if (endpoint.Name == "http") endpoint.TargetPort = 8080;
                }
            }
        }).PushToRegistry("scr.sheepreaper.xyz", "ihfiction/migrations");

        fictionApi.PublishAsDockerComposeService((res, service) =>
        {
            service.Environment.Remove("ConnectionStrings__fiction-db");
            service.Environment.Remove("ConnectionStrings__stories-db");

            if (builder.Configuration["OidcAuthority"] is string authority)
                service.Environment["OidcAuthority"] = authority;

            if (builder.Configuration["SecretsPath"] is string secretsPath)
                service.Environment["SecretsPath"] = secretsPath;

            service.Deploy ??= new();

            // BUG: This should be set automatically via ReplicaAnnotation
            service.Deploy.Replicas = res.GetReplicaCount();

            service.Networks.Add(FrontEndNetwork);
            service.Secrets.Add(new() { Source = "ConnectionStrings__fiction-db" });
            service.Secrets.Add(new() { Source = "ConnectionStrings__stories-db" });
            service.Secrets.Add(new() { Source = "KeycloakAdminClientOptions__AuthClientSecret" });

            if (builder.Configuration["Registry"] is string registry)
                service.Image = $"{registry}/{service.Name}:latest";

            if (res.TryGetEndpoints(out var endpoints))
            {
                foreach (var endpoint in endpoints)
                {
                    if (endpoint.Name == "https") res.Annotations.Remove(endpoint);
                    if (endpoint.Name == "http") endpoint.TargetPort = 8080;
                }
            }
        }).PushToRegistry("scr.sheepreaper.xyz", "ihfiction/fiction");

        webClient.PublishAsDockerComposeService((res, service) =>
        {
            if (builder.Configuration["OidcAuthority"] is string authority)
                service.Environment["OidcAuthority"] = authority;

            if (builder.Configuration["SecretsPath"] is string secretsPath)
                service.Environment["SecretsPath"] = secretsPath;

            service.Deploy ??= new();

            // BUG: This should be set automatically via ReplicaAnnotation
            service.Deploy.Replicas = res.GetReplicaCount();

            service.Networks.Add(FrontEndNetwork);
            service.Secrets.Add(new() { Source = "Authentication__Schemes__Keycloak__ClientSecret" });

            if (builder.Configuration["Registry"] is string registry)
                service.Image = $"{registry}/{service.Name}:latest";
        }).PushToRegistry("scr.sheepreaper.xyz", "ihfiction/web");

        return builder;
    }
}
