### AuthenticationHandler

```csharp
public class ApiKeyAuthentication : IAuthenticationHandler
{
  /*
    * This project uses Microsoft Orleans. Orleans grains make fantastic
    * storage and retrieval for api key information, so we use a grain-per-key.
    * Since grains are conveniently accessed by a guid key, it was decided that
    * an api key should be the string representation of a guid.
    */

  public const string SCHEME_NAME = "ApiKey";
  public const string SCHEME_DISPLAY_NAME = "ApiKey Authentication";

  private const string AUTHORIZATION_HEADER_PREFIX = SCHEME_NAME + " ";

  // Nothing to see here, folks. Nothing at all, moving rrriiiggght along now.
  private static readonly Guid _testDeveloperGuid = Guid.Parse("36F21412-7A50-4896-8ADC-2F6B3D4A3FC3");

  /// <summary>
  /// Used to access the Orleans grains that store Api Key information.
  /// </summary>
  private readonly IGrainFactory _grainFactory;

  private HttpContext _context;
  private AuthenticationScheme _scheme;

  public ApiKeyAuthentication(IGrainFactory grainFactory)
  {
    _grainFactory = grainFactory;
  }

  public Task InitializeAsync(AuthenticationScheme scheme, HttpContext context)
  {
    if (scheme.Name != SCHEME_NAME)
      throw new Exception($"{nameof(ApiKeyAuthentication)} class cannot handle scheme '{scheme.Name}'.");

    _scheme = scheme;
    _context = context;
    return Task.CompletedTask;
  }

  public async Task<AuthenticateResult> AuthenticateAsync()
  {
    try
    {
      if (TryGetTokenAsGuid(_context, out var token))
      {
        // You did not see this here.
        if (token == _testDeveloperGuid)
        {
          var identity = new ClaimsIdentity(SCHEME_NAME);
          identity.AddClaim(new Claim(ClaimTypes.NameIdentifier, _testDeveloperGuid.ToString("N")));
          identity.AddClaim(new Claim(ClaimTypes.Expiration, TimeStamp.MaxValue.TicksUtc.ToString()));
          var principle = new ClaimsPrincipal(identity);
          var authenticationTicket = new AuthenticationTicket(principle, SCHEME_NAME);
          return AuthenticateResult.Success(authenticationTicket);
        }
        else
        {
          // Get information about the api key from the Orleans grain that
          // stores it.
          var tokenData = await _grainFactory.GetGrain<IApiKeyGrain>(token).GetData().ConfigureAwait(false);

          if (tokenData is null)
            throw new Exception($"Api key '{token}' does not exist.");

          if (tokenData.Expiry <= TimeStamp.Now)
            throw new Exception($"Api key '{token}' has expired.");

          var identity = new ClaimsIdentity(SCHEME_NAME);
          identity.AddClaim(new Claim(ClaimTypes.NameIdentifier, tokenData.UserId.ToString("N")));
          identity.AddClaim(new Claim(ClaimTypes.Expiration, tokenData.Expiry.TicksUtc.ToString()));
          var principle = new ClaimsPrincipal(identity);
          var authenticationTicket = new AuthenticationTicket(principle, SCHEME_NAME);
          return AuthenticateResult.Success(authenticationTicket);
        }
      }
    }
    catch (Exception x)
    {
      return AuthenticateResult.Fail(x.Message);
    }

    return AuthenticateResult.NoResult();

    static bool TryGetTokenAsGuid(HttpContext context, out Guid token)
    {
      try
      {
        var authHeader = context.Request.Headers["Authorization"];
        for (var i = 0; i < authHeader.Count; i++)
        {
          if (authHeader[i].StartsWith(AUTHORIZATION_HEADER_PREFIX))
          {
            var tokenSpan = authHeader[i].AsSpan().Slice(AUTHORIZATION_HEADER_PREFIX.Length);
            token = Guid.Parse(tokenSpan);
            return true;
          }
        }
        token = Guid.Empty;
        return false;
      }
      catch (Exception x)
      {
        var message = "Unable to parse Authorization header. See inner exception for details.";
        throw new Exception(message, x);
      }
    }
  }

  // Used when user is not known (authentication did not succeed).
  public Task ChallengeAsync(AuthenticationProperties? properties)
  {
    _context.Response.StatusCode = StatusCodes.Status401Unauthorized;
    return Task.CompletedTask;
  }

  // Used when user is known (authentication succeeded), but the permission is
  // denied.
  public Task ForbidAsync(AuthenticationProperties? properties)
  {
    _context.Response.StatusCode = StatusCodes.Status403Forbidden;
    return Task.CompletedTask;
  }
}
