**IUserResolverService**
```cs
public class UserResolverService : IUserResolverService
    {
        private readonly UserManager<SBUser> _userManager;
        private readonly IHttpContextAccessor _context;


        public UserResolverService(UserManager<SBUser> userManager, IHttpContextAccessor context)
        {
            _userManager = userManager;
            _context = context;
        }
        public async Task<SBUser> ResolveAsync()
        {
            return await _userManager.GetUserAsync(_context.HttpContext.User);
        }
