        {
            var methodInfo = typeof(JsonConverter<T>).GetMethod(nameof(WriteNumberWithCustomHandling), BindingFlags.Instance | BindingFlags.NonPublic)!;
            var paramJsonConverter = Expression.Parameter(typeof(JsonConverter<T>));
