~~~ c#
    public class Test
    {
        public int MyProperty1 { get; set; } = 69;

        public int MyProperty2 { get; } = 420;

        public IEnumerable<int> Collection1 { get; set; } = new int[] { 69, 420 };

        public IEnumerable<int> Collection2 { get; } = new int[] { 69, 420 };
