+				mono_marshal_find_bitfield_offset (MonoVTable, initialized, &byte_offset, &bitmask);
+
+			ppc_lbz (code, ppc_r0, byte_offset, ins->sreg1);
+			ppc_andid (code, ppc_r0, ppc_r0, bitmask);
+			br = code;
+			ppc_bc (code, PPC_BR_FALSE, PPC_BR_EQ, 0);
+			mono_add_patch_info (cfg, code - cfg->native_code, MONO_PATCH_INFO_JIT_ICALL_ID,
+			     GUINT_TO_POINTER (MONO_JIT_ICALL_mono_generic_class_init));
+			if ((FORCE_INDIR_CALL || cfg->method->dynamic) && !cfg->compile_aot) {
+				ppc_load_func (code, PPC_CALL_REG, 0);
+#if defined(TARGET_POWERPC64) && !defined(PPC_USES_FUNCTION_DESCRIPTOR)
+				ppc_ldr (code, PPC_CALL_REG, 0, PPC_CALL_REG);
+				cfg->thunk_area += THUNK_SIZE;
+#endif
+				ppc_mtlr (code, PPC_CALL_REG);
+				ppc_blrl (code);
+			} else {
+				ppc_bl (code, 0);
+			}
+			ppc_patch (br, code);
+
+			ins->flags |= MONO_INST_GC_CALLSITE;
+			ins->backend.pc_offset = code - cfg->native_code;
+			break;
+		}
 		case OP_BR:
 			/*if (ins->inst_target_bb->native_offset) {
 				ppc_b (code, 0);
diff -u -r a/runtime/src/mono/mono/mini/mini-ppc.h b/runtime/src/mono/mono/mini/mini-ppc.h
--- a/runtime/src/mono/mono/mini/mini-ppc.h	2022-10-14 18:40:27.259521479 +0000
+++ b/runtime/src/mono/mono/mini/mini-ppc.h	2022-10-14 18:34:21.194384879 +0000
@@ -118,6 +118,7 @@
 #define MONO_ARCH_EMULATE_LCONV_TO_R8_UN 1
 #define MONO_ARCH_EMULATE_FREM 1
 #define MONO_ARCH_GC_MAPS_SUPPORTED 1
+#define MONO_ARCH_HAVE_OP_GENERIC_CLASS_INIT 1
 
 /* Parameters used by the register allocator */
 #define MONO_ARCH_CALLEE_REGS ((0xff << ppc_r3) | (1 << ppc_r12) | (1 << ppc_r11))
```

> @nealef , @janani66 having looked at this, I now think this isn't a problem - on s390x, we use a _call-saved_ register (r9) as IMT reg, and therefore we need to take care to not use it for the global allocator. But ppc uses a _call-clobbered_ register (r11), which is never used for the global allocator anyway. So this isn't the problem (unfortunately).

But `ppc_r11` appears to be used a temporary register in the JIT. For example:
```
               case OP_STOREI1_MEMBASE_REG:
                        if (ppc_is_imm16 (ins->inst_offset)) {
                                ppc_stb (code, ins->sreg1, ins->inst_offset, ins->inst_destbasereg);
                        } else {
                                if (ppc_is_imm32 (ins->inst_offset)) {
                                        ppc_addis (code, ppc_r11, ins->inst_destbasereg, ppc_ha(ins->inst_offset));  <----
                                        ppc_stb (code, ins->sreg1, ins->inst_offset, ppc_r11);
                                } else {
                                        ppc_load (code, ppc_r0, ins->inst_offset);
                                        ppc_stbx (code, ins->sreg1, ins->inst_destbasereg, ppc_r0);
                                }
                        }
```

```
# cat fsharpfix.patch
diff -u -r a/runtime/src/mono/mono/mini/mini-ppc.c d/runtime/src/mono/mono/mini/mini-ppc.c
--- a/runtime/src/mono/mono/mini/mini-ppc.c	2022-10-14 14:32:56.563588276 +0000
+++ d/runtime/src/mono/mono/mini/mini-ppc.c	2022-10-18 14:41:52.948439091 +0000
@@ -3782,23 +3782,11 @@
 				ppc_addis (code, ppc_r12, cfg->frame_reg, ppc_ha(cfg->stack_usage));
 				ppc_addi (code, ppc_r12, ppc_r12, cfg->stack_usage);
 			}
-			if (!cfg->method->save_lmf) {
-				pos = 0;
-				for (i = 31; i >= 13; --i) {
-					if (cfg->used_int_regs & (1 << i)) {
-						pos += sizeof (target_mgreg_t);
-						ppc_ldptr (code, i, -pos, ppc_r12);
-					}
-				}
-			} else {
-				/* FIXME restore from MonoLMF: though this can't happen yet */
-			}
 
 			/* Copy arguments on the stack to our argument area */
 			if (call->stack_usage) {
 				code = emit_memcpy (code, call->stack_usage, ppc_r12, PPC_STACK_PARAM_OFFSET, ppc_sp, PPC_STACK_PARAM_OFFSET);
 				/* r12 was clobbered */
-				g_assert (cfg->frame_reg == ppc_sp);
 				if (ppc_is_imm16 (cfg->stack_usage)) {
 					ppc_addi (code, ppc_r12, cfg->frame_reg, cfg->stack_usage);
 				} else {
@@ -3809,6 +3797,18 @@
 				}
 			}
 
+			if (!cfg->method->save_lmf) {
+				pos = 0;
+				for (i = 31; i >= 13; --i) {
+					if (cfg->used_int_regs & (1 << i)) {
+						pos += sizeof (target_mgreg_t);
+						ppc_ldptr (code, i, -pos, ppc_r12);
+					}
+				}
+			} else {
+				/* FIXME restore from MonoLMF: though this can't happen yet */
+			}
+
 			ppc_mr (code, ppc_sp, ppc_r12);
 			mono_add_patch_info (cfg, (guint8*) code - cfg->native_code, MONO_PATCH_INFO_METHOD_JUMP, call->method);
 			cfg->thunk_area += THUNK_SIZE;
```

Thanks @nealef and @uweigand  -- this patch fixed the problem!

